print(f"{high_vowels} ∩ {back_vowels} = {high_vowels & back_vowels}")

{'u', 'i', 'ɪ', 'ʊ'} ∩ {'u', 'ɑ', 'ɔ', 'o', 'ʊ'} = {'u', 'ʊ'}


